package vo;

public class ItemsViewVo {
	public ItemsViewVo() {
		// TODO Auto-generated constructor stub
	}
	
	int p_no, p_price, p_sale, i_no, s_amt;
	String p_name, p_date, p_country, color_name, size_name;
	
	public int getP_no() {
		return p_no;
	}
	public void setP_no(int p_no) {
		this.p_no = p_no;
	}
	public int getP_price() {
		return p_price;
	}
	public void setP_price(int p_price) {
		this.p_price = p_price;
	}
	public int getP_sale() {
		return p_sale;
	}
	public void setP_sale(int p_sale) {
		this.p_sale = p_sale;
	}
	public int getI_no() {
		return i_no;
	}
	public void setI_no(int i_no) {
		this.i_no = i_no;
	}
	public int getS_amt() {
		return s_amt;
	}
	public void setS_amt(int s_amt) {
		this.s_amt = s_amt;
	}
	public String getP_name() {
		return p_name;
	}
	public void setP_name(String p_name) {
		this.p_name = p_name;
	}
	public String getP_date() {
		return p_date;
	}
	public void setP_date(String p_date) {
		this.p_date = p_date;
	}
	public String getP_country() {
		return p_country;
	}
	public void setP_country(String p_country) {
		this.p_country = p_country;
	}
	public String getColor_name() {
		return color_name;
	}
	public void setColor_name(String color_name) {
		this.color_name = color_name;
	}
	public String getSize_name() {
		return size_name;
	}
	public void setSize_name(String size_name) {
		this.size_name = size_name;
	}
	
	
}
