package vo;

public class StockVo {
	int stock_no, s_amt, i_no;
	String s_regdate;
	String p_name, color_name, size_name;
	
	public StockVo() {
		// TODO Auto-generated constructor stub
	}
	
	public int getStock_no() {
		return stock_no;
	}
	public void setStock_no(int stock_no) {
		this.stock_no = stock_no;
	}
	public int getS_amt() {
		return s_amt;
	}
	public void setS_amt(int s_amt) {
		this.s_amt = s_amt;
	}
	public int getI_no() {
		return i_no;
	}
	public void setI_no(int i_no) {
		this.i_no = i_no;
	}
	public String getS_regdate() {
		return s_regdate;
	}
	public void setS_regdate(String s_regdate) {
		this.s_regdate = s_regdate;
	}

	public String getP_name() {
		return p_name;
	}

	public void setP_name(String p_name) {
		this.p_name = p_name;
	}

	public String getColor_name() {
		return color_name;
	}

	public void setColor_name(String color_name) {
		this.color_name = color_name;
	}

	public String getSize_name() {
		return size_name;
	}

	public void setSize_name(String size_name) {
		this.size_name = size_name;
	}
	
	
}
